insert into PATTERN (id, name, alternativeName, description, longDescription, type) values (1, 'Abstract factory', 'Factory of factories,kit', 'Provide an interface for creating families of related or dependent objects without specifying their concrete classes.', 'In Abstract Factory pattern an interface is responsible for creating a factory of related objects, without explicitly specifying their classes. Each generated factory can give the objects as per the Factory pattern', 'creational')
insert into PATTERN (id, name, alternativeName, description, longDescription, type) values (2, 'Factory', '', 'The factory is an object for creating other objects','In Factory pattern, we create object without exposing the creation logic to the client and refer to newly created object using a common interface', 'creational')
insert into PATTERN (id, name, alternativeName, description, longDescription, type) values (3, 'Factory method', 'Virtual constructor', 'Define an interface for creating an object, but let the classes that implement the interface decide which class to instantiate','The factory method pattern relies on inheritance, as object creation is delegated to subclasses that implement the factory method to create objects', 'creational')